mapper.select:
/*selectBoardList*/ select a.SEQ , a.BBS_ID , a.TYPE_ETC , a.TITLE , a.CONTENTS , a.FILE_ID , a.INPUT_ID , a.INPUT_NM , a.INPUT_DATE , a.EDIT_ID , a.EDIT_NM , a.EDIT_DATE , a.HIT , a.ORDERS from 120BPM_BOARD a where a.DEL_YN = 'N' and a.BBS_ID = #{bbsId}
AND a.TITLE LIKE CONCAT('%', #{searchWord}, '%')
order by SEQ desc limit #{index}, #{count};

mapper.update:
/*updateBoard*/
update 120BPM_BOARD set

		OPEN_YN = #{openYn}


		, TYPE_ETC = #{typeEtc}


		, TITLE = #{title}


		, CONTENTS = #{contents}


		, FILE_ID = #{fileId}


		, EDIT_ID = #{editId}


		, EDIT_NM = #{editNm}

		, EDIT_DATE = NOW()
where
	SEQ = #{seq}

/*incrementBoardViews*/
update 120BPM_BOARD set
	HIT = HIT+1
where SEQ = #{seq}

mapper.insert:
/*insertBoard*/
insert into 120BPM_BOARD(
					 BBS_ID
				   , OPEN_YN
				   , TYPE_ETC
				   , TITLE
				   , CONTENTS
				   , FILE_ID
				   , INPUT_ID
				   , INPUT_NM
				   , INPUT_DATE
				   , HIT
				   , DEL_YN
) values(
			#{bbsId}
		, #{openYn}
		, #{typeEtc}
		, #{title}
		, #{contents}
		, #{fileId}
		, #{inputId}
		, #{inputNm}
		, NOW()
		, '0'
		, 'N'
		)

mapper.update:
/*deleteBoard*/
update 120BPM_BOARD set
	OPEN_YN = N
	, EDIT_NM = #{editNm}
	, EDIT_DATE = NOW()
where
SEQ = #{seq}
